class Solution {

    private int helper(int row, int cols, int[][] mat, int target, int sum,int[][] dp){
        if(row==-1)
            return Math.abs(sum-target);
        if(dp[row][sum]!= -1)
            return dp[row][sum];
        int min = (int) 1e8;
        for(int i=0;i<cols;i++){
            int val = helper(row-1,cols,mat,target,sum+mat[row][i],dp);
            min = Math.min(val,min);
        }
        return dp[row][sum]=min;

    }
    public int minimizeTheDifference(int[][] mat, int target) {
        int m = mat.length;
        int n = mat[0].length;
        int[][] dp = new int[m][9999];
        for(int[] row: dp)
            Arrays.fill(row,-1);
        return helper(m-1,n,mat,target,0,dp);
    }
}

