impl Solution {
    pub fn minimize_the_difference(mat: Vec<Vec<i32>>, target: i32) -> i32 {
        let n = mat.len();
        let m = mat[0].len();
        let max = 70*70;

        let mut dp = vec![vec![false; max+1 as usize]; n+1];

        dp[0][0] = true;

        for i in 0..n {
            for j in 0..m {
                for k in 0..max+1 {
                    if dp[i][k] {
                        dp[i+1][k + mat[i][j] as usize] = true;
                    }
                }
            }
        }

        let result = dp[n].iter().enumerate().filter(|x| *x.1).map(|x| (target - x.0 as i32).abs()).min().unwrap();
        return result;
    }
}
