class Solution {
public:
    int dp[80][4902];
    int solve(int row, vector<vector<int>> &mat, int &target, int sum, int &m, int &n) {
        if(row==m) {
            int ab = abs(target-sum);
            // ans = min(ans,ab);
            return ab;
        }
        if(dp[row][sum]!=-1) return dp[row][sum];
        int ans = INT_MAX;
        for(int col=0;col<n;col++) {
            ans = min(ans,solve(row+1,mat,target,sum+mat[row][col],m,n));
        }

        return dp[row][sum] = ans;
    }
    int minimizeTheDifference(vector<vector<int>>& mat, int target) {
        int m = mat.size();
        int n = mat[0].size();
        memset(dp,-1,sizeof(dp));
        int ans = solve(0,mat,target,0,m,n);

        return ans;
    }
};
