        if(row==mat.length){
            return Math.abs(sum-target);
        }
        if(memo[sum][row]!=null)
            return memo[sum][row];
        int min = Integer.MAX_VALUE;
        for(int i=0;i<mat[0].length;i++){
            min=Math.min(min,minimize(mat,target,sum+mat[row][i],row+1));
        }
        memo[sum][row]=min;
        return min;
    }
}
