class Solution {
    public int minimizeTheDifference(int[][] mat, int target) {

        int m = mat.length, n = mat[0].length;

        Integer[][] dp = new Integer[m][5001];

        for(int[] g : mat){
            Arrays.sort(g);
        }
        int min = helper(mat, 0, target, 0, dp);
        return min;
    }

    public int helper(int[][] mat, int row, int target, int current, Integer[][] dp){
        if(row == mat.length){
            return Math.abs(target - current);
        }

        if(dp[row][current] != null)
            return dp[row][current];

        int min = Integer.MAX_VALUE; 
        for(int i = 0 ; i < mat[0].length && min != 0; i++){
            min = Math.min(min, helper(mat, row + 1, target, current + mat[row][i], dp));
        }
        return dp[row][current] = min;
    }
}
