class Solution {
public:
    const int offset = 5000;
    int dfs(int r, int diff, vector<vector<int>>& mat, vector<vector<int> >& dp) {
        if(r == mat.size()) return abs(diff);
        if(dp[r][diff + offset] != INT_MAX) return dp[r][diff + offset];

        int ans = INT_MAX;
        if(diff > 0) {
            for(int c=0; c<mat[0].size(); c++) {
                int sub = dfs(r+1, diff - mat[r][c], mat, dp);
                ans = min(ans, sub);
            }
        } else {
		    // At row r, the sum of selected numbers from row 0 till r-1 is already equal or greater than target.
			// From row r, we simply pick the smallest number of each row.
            int minnum = mat[r][0];
            for(int c=0; c<mat[0].size(); c++) minnum = min(minnum, mat[r][c]);
            ans = dfs(r+1, diff - minnum, mat, dp);
        }
        dp[r][diff+offset] = ans;
        return ans;
    }

    int minimizeTheDifference(vector<vector<int>>& mat, int target) {
        int m = mat.size();
        vector<vector<int> > dp(m, vector<int>(target + offset + 1, INT_MAX));

        return dfs(0, target, mat, dp);
    }
};
