class Solution {
     int ans = Integer.MAX_VALUE;
    public int minimizeTheDifference(int[][] mat, int target) {
        int n = mat.length;
        int m = mat[0].length;
        // Sort each of the columns for Prunning
        for(int[] cols: mat) {
            Arrays.sort(cols);
        }
        boolean[][] visited = new boolean[n][4900];
        backtrack(mat, target, visited, 0, 0);
        return ans;
    }
    public void backtrack(int[][] mat, int target, boolean[][] visited, int row, int pathSum) {
        if(row == mat.length) {
            ans = Math.min(Math.abs(target-pathSum), ans);
            return;
        }
        if(visited[row][pathSum])
            return;
        for(int j=0; j< mat[0].length; j++) {
            backtrack(mat, target, visited, row+1, pathSum+mat[row][j]);
            // Pruning:
            // we don't need to travel other paths of pathSum+mat[row][j] is already greater than the target because this won't give us the min absolute diffrent now
            if(pathSum+mat[row][j] > target)
                break;
        }
        visited[row][pathSum] = true;
    }
}
