class Solution {
    public int minimizeTheDifference(int[][] mat, int target) {

        for (int i = 0; i < mat.length; i++) {
            Arrays.sort(mat[i]);
        }

        int m = mat.length;
        int n = mat[0].length;

        boolean[][] dp = new boolean[m][4901];
        int ret = Integer.MAX_VALUE;


        int sum = mat[0][n-1];

        for (int k = 0; k < n; k++) {
            dp[0][mat[0][k]] = true;
        }


        for (int i = 1; i< m; i++) {
            sum += mat[i][n-1];

            for (int j = 0; j <= sum; j++) { 

                for (int k = 0; k < n; k++) {
                    if (j >= mat[i][k]) dp[i][j] = dp[i-1][j-mat[i][k]];
                    else break;

                    if (dp[i][j]) break;
                }

            }
        }


        for (int j = 0; j <= 4900; j++) {
            if (dp[m-1][j]) ret = Math.min(ret, Math.abs(j-target));
        }
        return ret;

    }
}
