class Solution {
public:
    int minimizeTheDifference(vector<vector<int>>& mat, int target) {

        int posmin = 0 ; 
        for(auto &row : mat) 
            posmin += *min_element(all(row)) ;

        if(posmin > target) return posmin - target ; 

        unordered_set<int> s = {0} ; 
        for(auto &row : mat){
            unordered_set<int> t ; 
