class Solution {
public:
    int dfs(vector<vector<int>>& nums,vector<vector<int>> &dp, int target,int in,int sum){
        if(in>=nums.size()) return abs(sum-target); // if reach end of the matrix, return the abs diff
        if(dp[in][sum]!=-1) return dp[in][sum];  // if this state is already been processed, return the result of this state.
        int re = INT_MAX;
        if(sum>target){  // if sum is already greater the target, than to minimise the abs diff, its better to pick minimum elements from further row now
            auto t = *min_element(nums[in].begin(),nums[in].end());
            return dp[in][sum] = dfs(nums,dp,target,in+1,sum+t);
        }
        for(int i = 0; i<nums[0].size();++i){  // try every index form in'th row and find the minimum abs difference
            re = min(re,dfs(nums,dp,target,in+1,sum+nums[in][i]));
        }
        return dp[in][sum] = re;
    }
    int minimizeTheDifference(vector<vector<int>>& mat, int target) {
        vector<vector<int>> dp(mat.size(),vector<int>(5005,-1));
        return dfs(mat,dp,target,0,0);
    }
};
