int minimizeTheDifference(vector<vector<int>>& mt, int target) {
        int n = mt.size(), m = mt[0].size();
		for (int i = 0; i < n; i++) sort(mt[i].begin(), mt[i].end());
		const int inf = 1e9;
		vector<vector<int>> dp(n, vector<int>(target + 1, inf));
		dp[0][0] = mt[0][0];
		for (int i = 1; i < n; i++) dp[i][0] = mt[i][0] + dp[i - 1][0];
		for (int t = 1; t <= target; t++) {
			dp[0][t] = mt[0][0];
			for (int j = 1; j < m; j++) {
				if (abs(t - mt[0][j]) < abs(t - dp[0][t]))
					dp[0][t] = mt[0][j];
			}
			for (int i = 1; i < n; i++) {
				dp[i][t] = dp[i][t - 1];
				for (int j = 0; j < m; j++) {
					int cur = mt[i][j] + dp[i - 1][max(0, t - mt[i][j])];
					if (abs(cur - t) < abs(dp[i][t] - t))
						dp[i][t] = cur;
				}
			}
		}
		return abs(dp[n - 1][target] - target);
