class Solution:
    def minimizeTheDifference(self, mat: List[List[int]], target: int) -> int:
        reachable_sum = 1
        for row in mat:
            c = 0
            for el in row:
                c |= (reachable_sum << el)
            reachable_sum = c
            print(f"reachable_sum is {reachable_sum}")

        min_diff = float("+inf")
        idx = 0
        print(f"reachable: {reachable_sum}; idx: {idx}")
        while reachable_sum:
            print(f"reachable_sum: {reachable_sum}, idx: {idx}, bool: {reachable_sum & 1}")
            if reachable_sum & 1:
                min_diff = min(min_diff, abs(target - idx))
            idx += 1
            reachable_sum >>= 1
            print(f"min_diff: {min_diff}")
        return min_diff
