import kotlin.math.abs
import kotlin.math.min

class Solution {
    private var m = 0
    private var n = 0
    private lateinit var mat: Array<IntArray>
    /**
     * 1st dimen index: index of starting row
     * map's:
     *  - key: new target
     *  - value: minimizeTheDifference
     * */
    private lateinit var ansMaps: Array<MutableMap<Int, Int>>


    fun minimizeTheDifference(mat: Array<IntArray>, target: Int): Int {
        m = mat.size
        n = mat.first().size

        ansMaps = Array(m) { HashMap<Int, Int>() }
        this.mat = mat

        // sort each row
        mat.forEach { it.sort() }

        val firstColSum = mat.sumBy { it.first() }

        return when {
            m == 1 -> mat.first().findMinAbs(target)
            n == 1 -> abs(firstColSum - target)
            target <= firstColSum -> firstColSum - target
            else -> {
                fillAnsMaps(0, target)
            }
        }
    }

    private fun fillAnsMaps(rowIndex: Int, target: Int): Int {
        val ansMap = ansMaps[rowIndex]

        if (!ansMap.containsKey(target)) {
            val row = mat[rowIndex]
            var ans = Int.MAX_VALUE
            if (rowIndex == mat.lastIndex) {
                ans = row.findMinAbs(target)
            } else {
                val nextRowIndex = rowIndex + 1
                val remainingRowCount = m - rowIndex
                when {
                    target <= remainingRowCount -> {
                        ans = min(ans, fillAnsMaps(nextRowIndex, target - row.first()))
                    }
                    target >= remainingRowCount * 70 -> {
                        ans = min(ans, fillAnsMaps(nextRowIndex, target - row.last()))
                    }
                    else -> {
                        row.forEach { cell ->
                            ans = min(ans, fillAnsMaps(nextRowIndex, target - cell))
                        }
                    }
                }
            }
            ansMap[target] = ans
        }

        return ansMap[target]!!
    }

    /** @receiver sorted, size >= 2 */
    private fun IntArray.findMinAbs(target: Int): Int {
        return when {
            size == 2 -> kotlin.math.min(abs(this[0] - target), abs(this[1] - target))
            target <= first() -> abs(target - first())
            target >= last() -> abs(target - last())
            else -> {
                // binary search
                var startIndex = 0
                var endIndex = lastIndex

                while (endIndex - startIndex >= 2) {
                    val midIndex = (startIndex + endIndex) / 2
                    val midValue = this[midIndex]
                    when {
                        midValue == target -> return 0
                        midValue < target -> startIndex = midIndex
                        target < midValue -> endIndex = midIndex
                    }
                }

                kotlin.math.min(abs(this[startIndex] - target), abs(this[endIndex] - target))
            }
        }
    }
}
```

