class Solution {
    TreeNode faultyA = null;
    TreeNode faultyB = null;
    TreeNode mPrevious = null;
    public void recoverTree(TreeNode root) {
        findFaultyNodes(root);

        //Swap faulty nodes
        if(faultyA != null && faultyB != null) {
            int temp = faultyA.val;
            faultyA.val = faultyB.val;
            faultyB.val = temp;
        }
    }

    private void findFaultyNodes(TreeNode node) {
        if(node == null) {
            return;
        }

        findFaultyNodes(node.left);
        if(mPrevious != null) {
            if(node.val < mPrevious.val) {
                if(faultyA == null) {
                    faultyA = mPrevious;
                    faultyB = node;
                } else {
                    faultyB = node;
                }
            }
        }
        mPrevious = node;
        findFaultyNodes(node.right);
    }
}

