class Solution {
public:
    TreeNode *f=0,*s=0,*prev=new TreeNode(INT_MIN);
    void recoverTree(TreeNode* root) {
       traverse(root);
        int t=f->val;
        f->val=s->val;
        s->val=t;
    }
    void traverse(TreeNode* root){
        if(!root)return;
        traverse(root->left);
		//if first element itself not found and and an order mis-match found in inorder, assing it to first element i.e. 'f'
        if(!f and prev->val>root->val)f=prev;

		//if first element already found and and an order mis-match found in inorder, assing it to second element i.e. 's'
        if(f and prev->val>root->val)s=root;
        prev=root;
        traverse(root->right);
    }
};
