    void recoverTree(TreeNode* root) {
        // when inorder traversing a BST, we would traverse the node with the values in increasing order
        // if there are only two nodes in the BST that their values are wrong, there's only one situation:
        // a node with a larger value appears before a node with a smaller value in the inorder traversal result
        // during the inorder traversing, the first node which value is greater than the its successor must be one of the wrong nodes which has a larger value
        // for the smaller value wrong node, we can see that its value must be smaller than its predecessor
        // we can see that the last node which value is smaller than its predecessor in the inorder traversal must be the smaller wrong node.
        // to better understanding we can see that for an inorder traversal result 1 2 6 4 5 3 7
        // the first node that its value greater than its successor is the node with value 6
        // and the last node which value is smaller than its predecessor is the node with value 3
        // for an other example, 1 3 2 4
        // 3 is the first value which is greater than its successor and 2 is the last value which value is smaller than its predecessor
        TreeNode* largeNode = NULL;
        TreeNode* smallNode = NULL;
        TreeNode* preNode = NULL;
        inorderTraversal(root, largeNode, smallNode, preNode);
        int smallVal = smallNode->val;
        smallNode->val = largeNode->val;
        largeNode->val = smallVal;
    }

    void inorderTraversal(TreeNode* node, TreeNode*& largeNode, TreeNode*& smallNode, TreeNode*& preNode) {
        if (node == NULL) {
            return;
        }
        inorderTraversal(node->left, largeNode, smallNode, preNode);
        // the first node has no predecessor so we can't tell whether it's greater or smaller than its predecessor
        if (preNode != NULL && node->val < preNode->val) {
            // largeNode equals NULL means preNode is the first node whose value is greater than its successor
            // so preNode is the wrong larger node
            if (largeNode == NULL) {
                largeNode = preNode;
            }
            // there maybe two nodes with a smaller value than its predecessor, the successor of the larger wrong node and the real smaller wrong node
            // the last smallNode is the smaller wrong node which we want to get
            smallNode = node;
        }
        // node becomes preNode of the next node after being traversed
        preNode = node;
        inorderTraversal(node->right, largeNode, smallNode, preNode);
    }
