        dfs(root -> left, vec);
        vec.push_back(root -> val);
        dfs(root -> right, vec);
    }
    void in(TreeNode* root, vector<int>& vec, int &i) {
        if(!root) return;
        // i++;
        in(root -> left, vec, i);
        if(root -> val != vec[i]) root -> val = vec[i];
        i++;
        in(root -> right, vec, i);
    }
