            root->val = a;

        swap(root->left);
        swap(root->right);
    }

    void recoverTree(TreeNode* root) {
        inorder(root);

        for(int i = 0; i < v.size()-1; i++) {
            if (v[i] > v[i+1]) {
                a = v[i];
                break;
            }
        }

        for(int i = v.size()-1; i > 0; i--) {
            if (v[i] < v[i-1]) {
                b = v[i];
                break;
            }
        }

        swap(root);
    }
};
```

