**Morris Inorder traversal way**
```
//Global variables
TreeNode* firstNode = NULL;
TreeNode* secondNode = NULL;
TreeNode* prevNode = NULL;

//Program starts here
void recoverTree(TreeNode* root) 
{
    //edge case - if 0 or 1 elements exist, return
    if(root == NULL or (root->left == NULL and root->right == NULL))
        return;        

    //Search the BST using inorder traversal
    inorder(root);

    //if there actually exists an anomaly... 
    if(firstNode and secondNode)
        //...fix it
        swap(firstNode->val, secondNode->val);
}

//-------------------------------

//Inorder using Morris Traversal
void inorder(TreeNode* root) 
{
    //Initialise a pointer to traverse the tree
    TreeNode* curr = root;

    while(curr != NULL)
    {
        //if there's no left subtree, all you need to do is...
        if(curr->left == NULL)
        {
            //...find anomalies and...
            findAnomaly(curr);
            //traverse the right subtree.
            curr = curr->right;
        }
        //But if there is a left subtree,
        else
        {
            //Create a new pointer on left subtree's root, 
            TreeNode* findRight = curr->left;

            //and go to the extreme right, until you find NULL or 
            //a thread (link) to curr
            while(findRight->right != NULL and findRight->right != curr)
            {
                findRight = findRight->right;
            }

            //If the extreme right is a NULL, create a thread to curr, 
            //because that's where we'll go after this subtree is traversed
            if(findRight->right == NULL)
            {
                //Create the thread (link) to curr
                findRight->right = curr;
                //Now curr can traverse the left subtree (safely, lol)
                curr = curr->left;
            }
            //If the extreme right was a link to curr, 
            //the left subtree has already been visited
            else 
            {
                //So remove the link to curr 
                findRight->right = NULL;
                //Find anomalies
                findAnomaly(curr);
                //And start traversing the right subtree
                curr = curr->right;
            }
        }
    }        
}

void findAnomaly(TreeNode* root)
{
    //if this isn't the first node you're checking 
    //and it's value is less than prevNode, then 
    //something's fishy!
    if(prevNode != NULL and prevNode->val > root->val)
    {
        //if you haven't found the first anomaly, then this is it!
        if(firstNode == NULL)
        firstNode = prevNode;

        //the current element may also be the anomaly
        secondNode = root;
    }

    //update prevNode with the current node
    prevNode = root;
}
