    TreeNode* prev = NULL;
    TreeNode* first = NULL;
    TreeNode* second = NULL;
    void inorder(TreeNode* root) {
        if(!root) return;
        inorder(root->left);
        if(!first && prev && prev->val >= root->val) first = prev;
        if(first && prev->val >= root->val) second = root;
