    void recoverTree(TreeNode* root) {
        vector<int>bs;
        inorder(root,bs);
        int a=-1,b=-1;
        int count=0;
        for(int i=0;i<bs.size()-1;i++){
            if(bs[i]>bs[i+1] && count==0){
                a=i;
                count++;
            }else if(bs[i]>bs[i+1] && count==1){
                b=bs[i+1];
                break;
            }
        }
        if(b==-1){
            b=bs[a+1];
        }
        a=bs[a];
        TreeNode* r;
        recover(root,a,b,r);
    }
};
