class Solution {
    TreeNode m1 = null;
    TreeNode m2 = null;
    boolean foundM1 = false;
    boolean foundM2 = false;
    public void recoverTree(TreeNode root) {
        inorderForward(root); // forward
        inorderBackward(root); // backward

        int temp = m1.val;
        m1.val = m2.val;
        m2.val = temp;
