class Solution {
    func recoverTree(_ root: TreeNode?) {

        var node: TreeNode! = root
        var prev: TreeNode!
        var parent: TreeNode!

        var swap1: TreeNode!
        var swap2: TreeNode!

        while node !== nil {

            if node.left === nil {
                if parent !== nil && node !== nil && parent.val > node.val {
                    if swap1 == nil { swap1 = parent }
                    swap2 = node
                }

                parent = node
                node = node.right
            } else {

                prev = node.left
