class Solution {
    func rightSideView(_ root: TreeNode?) -> [Int] {
        guard let root = root else { return [] }
        var queue = [root]
        var ans = [root.val]
        while !queue.isEmpty {
            let count = queue.count
            var added = false
            for i in 0..<count {
                let curr = queue.removeFirst()
                if let val = (curr.right?.val ?? curr.left?.val), !added {
                    ans.append(val)
                    added = true
                }
                if let right = curr.right {
                    queue.append(right)
                }
                if let left = curr.left {
                    queue.append(left)
                }
            }
        }
        return ans
    }
}
