                let node = queue.removeFirst()
                if count == 0 { result.append(node.val) }
                if let leftChild = node.left { queue.append(leftChild) }
                if let rightChild = node.right  { queue.append(rightChild) }
                count -= 1
            }
        }

        return result
    }
