vector<int> rightSideView(TreeNode* root) 
{
    vector<int> ret;        // This is the vector we will return
    queue<TreeNode*> q;     // This is how we will track levels

    if(!root)               // If we have no root, we see nothing
        return ret;

    q.push(root);           // Push root node onto the queue

    while(!q.empty())       // Loop until we're out of nodes
    {
        ret.push_back(q.front()->val);  // Ever time we reach this line, we will be on the right most node

        int count = q.size();           // This is to make sure that we break at every level

        while(count > 0)                // While node at a given level are in the queue
        {
            if(q.front()->right)            // Enqueue their children right to left
                q.push(q.front()->right);
            if(q.front()->left)
                q.push(q.front()->left);

            q.pop();                        // Pop the node who's children you enqueued off

            count--;                        // decrese the level count
        }

        // Because we enqueued right to left, the right most node will be at the front
        // of the queue, so when we reset to the top of the while loop, we record it
    }

    return ret;
}

