class Solution {
public:
    vector<int> rightSideView(TreeNode* root) {
        vector<int> ans;
        queue<TreeNode*> q; // bfs queue 
        if(root) q.push(root);
        while(!q.empty()){
            int l=q.size();
            ans.push_back(0);
            // look at all nodes at the current level 
            while(l--){
                TreeNode* node = q.front();
                q.pop();
                if(node->left)
                    q.push(node->left);
                if(node->right)
                    q.push(node->right);
                ans[ans.size()-1]=node->val; // at the end of the loop, the right most value at the current level will be there at that position.
            }
        }
        return ans;
    }
};
