class Solution {
public:
    vector<int> rightSideView(TreeNode* root) {
        vector<int> ans;
        if(root == NULL) return ans;
        // queue to store the nodes
        queue<TreeNode*> q;

        // push the root node first
        q.push(root);

        // iterate till q is not empty
        while(!q.empty())
        {
            // check the size of the queue which will be equal to the
            // number of nodes in that particular level
            int n = q.size();
            int val;
            for(int i=0; i<n; i++)
            {
                TreeNode* cur = q.front();
                q.pop();
                // update the val variable such that
                // it always stores the last node of that level
                val = cur->val;

                // check if left child is not null and push into queue
                if(cur->left != NULL) q.push(cur->left);
                // check if right child is not null and push into queue
                if(cur->right != NULL) q.push(cur->right);
            }

            // append val to the vector
            ans.push_back(val);
        }

        return ans;
    }
};
```

