		public List<Integer> rightSideView(TreeNode root) {
			int height = 0; 
			List<Integer> ans = new ArrayList<>();
			if(root == null) return ans;

        Node newRoot = new Node(root, height);

        Deque<Node> stack = new ArrayDeque<>();

        stack.push(newRoot);

        while(!stack.isEmpty()){
            Node n = stack.pop();
            TreeNode cur = n.node;
            height = n.height;

            if(height == ans.size()) ans.add(cur.val);


            if(cur.left != null) stack.push(new Node(cur.left, height + 1));
            if(cur.right != null) stack.push(new Node(cur.right, height + 1));

        }
        return ans;
    }
