class Solution {
public:
    int n;
    //just to initialise the size of our result vector
    //height = number of elements in the vector
    //Geeks for Geeks function code for height of a tree
    int maxDepth(TreeNode* node){ 
        if (!node) return 0;
        else{ 
            /* compute the depth of each subtree */
            int lDepth = maxDepth(node->left); 
            int rDepth = maxDepth(node->right); 

            /* use the larger one */
            if (lDepth > rDepth) 
                return(lDepth + 1); 
            else return(rDepth + 1); 
        } 
    }
    //all we have to do is to use the rightmost node in every level
    //we keep updating the index with the newest value obtained
    //which is the rightmost since we traverse from left to right
    void fill(vector<int> &nums, TreeNode* root, int level){
        //base case
        if(level == n) return;
        if(!root) return;
        //recursive case
        nums[level] = root->val;
        fill(nums, root->left, level+1);
        fill(nums, root->right, level+1);
        return;
    }
    vector<int> rightSideView(TreeNode* root) {
        n = maxDepth(root);
        if(!n) return {};
        vector<int> res(n);
        fill(res, root, 0);
        return res;
    }
};
