//LOGIC::
// AFTER ADDING A LEVEL IN QUEUE LAST ELEMENT WILL ALWAYS BE RIGHT SIDE ELEMENT SO WHEN I REACHES LAST ELEMENT IN QUEUE IT WILL GIVE US RIGHT SIDE ELEMENT IN  BINARY TREE
class Solution {
    public List<Integer> rightSideView(TreeNode root) {
        List<Integer> list=new ArrayList<>();
        if(root==null)
        {
            return new ArrayList<>();
        }
        Queue<TreeNode> q=new LinkedList<>();
        q.offer(root);
        while(!q.isEmpty())
        {
            int n=q.size();
            for(int i=0;i<n;i++)
            {
                TreeNode t=q.poll();
                 if(i==n-1)
                {
                    list.add(t.val);
                }
                if(t.left!=null)
                {
                    q.add(t.left);
                }
                if(t.right!=null)
                {
                    q.add(t.right);
                }
            }
        }
        return list;
    }
}
