				p = ans;
			}
			else {
				p->next = (struct ListNode*)malloc(sizeof(struct ListNode));
				p->next->val = 0;
				p = p->next;
			}
			int a = p1 != 0 ? p1->val : 0;
			int b = p2 != 0 ? p2->val : 0;
			int s = (a + b + c) % 10;
			c = (a + b + c) / 10;
			p->val = s;
			p->next = 0;
			p1 = p1 == 0 ? 0 : p1->next;
			p2 = p2 == 0 ? 0 : p2->next;
		}
		return ans;
