                result->next = p1;
                break;
            }
            int digit1 = p1 ? p1->val : 0;
            int digit2 = p2 ? p2->val : 0;
            int sum = digit1 + digit2 + carry;
            carry = sum / 10;
            sum %= 10;
            result->next = new ListNode(sum);
            if(p1)  p1 = p1->next;
            if(p2)  p2 = p2->next;
            result = result->next;
        }
        result = dummy->next;
        delete dummy;
        return result;
    }
};
```

