            if(l1)l1->val = sum%10; // anyone (l1 or l2) could be the ans
            if(l2)l2->val = sum%10;
            extra = sum/10;
            if(l1)l1 = l1->next, flag = 1;
            if(l2)l2 = l2->next, flag = 2;
        }
        ans = flag == 1 ? head1 : head2;
        if(extra == 0)return ans;

        // if there is extra
        ListNode* temp = ans;
        while(temp->next)temp = temp->next; // go to the last node and add the extra
        temp->next = new ListNode(extra);

        return ans;
    }
