class Solution {
public:
    ListNode* addTwoNumbers(ListNode* a, ListNode* b) {
        int carry = 0;
        ListNode *result = nullptr, *temp = nullptr, *prev = nullptr;
        while(a or b){
            int sum = carry + (a ? a->val : 0) + (b ? b->val : 0);
            carry = sum >= 10;
            sum %= 10;
