            if(l2 != nullptr) {b = l2->val; l2 = l2->next;}
            else b = 0;
            ListNode *temp = new ListNode((a + b + carry) % 10);
            carry = (a + b + carry) / 10;
            head->next = temp;
            head = head->next;

        }
        return ans.next;
    }
};
