class Solution {

    int carry = 0;
    ListNode result = null;
    ListNode curr = null;

    int getSize(ListNode temp)
    {
        int length = 0;
        while(temp != null)
        {
            length++;
            temp = temp.next;
        }
        return length;
    }
    public ListNode addTwoNumbers(ListNode l1, ListNode l2) {

        if(l1 == null)
            return l2;
        if(l2 == null)
            return l1;

        if(getSize(l2) > getSize(l1))
        {
            ListNode temp = l1;
            l1 = l2;
            l2 = temp;
        }
        result = l1;
        ListNode prevl1 = l1;

        while(l1 != null)
        {
            int x = l1.val;
            int y = l2 != null ? l2.val : 0;
            int sum = x + y + carry;
            carry = sum/10;
            sum = sum%10;
            l1.val = sum;
            prevl1 = l1;
            l1 = l1.next;
            if(l2 != null)
               l2 = l2.next;
        }
        if(carry > 0)
        {
            prevl1.next = new ListNode(carry);
        }
      return result;
    }
}

