class Solution {
    public ListNode addTwoNumbers(ListNode l1, ListNode l2) {

//variables        
        int carry = 0; int sum = 0; int d1 = 0; int d2 = 0; int d3 =0;

//sizes
        ListNode temp1 = l1; int size1 = 0; while(temp1 != null) { temp1 = temp1.next;size1++; }
        ListNode temp2 = l2; int size2 = 0; while(temp2 != null) { temp2 = temp2.next;size2++; }
        ListNode l3 = null; int size3 = Math.max(size1,size2);

//calculation 
       for(int i=1;i<=size3;i++)
       {
           if(l1 == null) { d1 = 0; } else { d1 = l1.val; } if(l2 == null) { d2 = 0;} else { d2 = l2.val; }
           sum = d1+d2+carry; d3 = sum%10; carry = sum/10;
           ListNode newnode = new ListNode(d3);
           if(l3 == null) { l3 = newnode;} else { newnode.next = l3; l3 = newnode;}
           if(l1 != null) { l1 = l1.next; } if ( l2 != null ) { l2 = l2.next; }
       }
//adding the node of remaining carry 
             if (carry != 0) { ListNode newnode = new ListNode(carry); newnode.next = l3; l3 = newnode; }

//reverse
     ListNode returned = null; ListNode temp3 = l3;
     while(temp3 != null ) { ListNode newnode = new ListNode(temp3.val);newnode.next = returned; returned = newnode; temp3 = temp3.next; } 

return returned;
    }
}

/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
 #include <bits/stdc++.h>
class Solution {
public:
    ListNode* addTwoNumbers(ListNode* l1, ListNode* l2) {
        long long int num1 = 1;
        ListNode* curr1 = l1;
        while(curr1!=NULL){
            curr1 = curr1->next;
            num1++;
        }
        curr1 = l1;

        long long int roh1 = 0;
        long long int i = num1;
        while(curr1!=NULL){
            roh1+= pow(10,num1-i)*(curr1->val);
            curr1 = curr1->next;
            i--;
        }

        long long int num2 = 1;
        ListNode* curr2 = l2;
        while(curr2!=NULL){
            curr2 = curr2->next;
            num2++;
        }
        curr2 = l2;

        long long int roh2 = 0;
        long long int j = num2;
        while(curr2!=NULL){
            roh2+= pow(10,num2-j)*(curr2->val);
            curr2 = curr2->next;
            j--;
        }

        long long int a = roh1+roh2;

        ListNode* newhead = new ListNode(a%10);
        a = a/10;
        ListNode* currnode = newhead;
        while(a!=0){
            ListNode* temp = new ListNode(a%10);
            a = a/10;
            currnode->next = temp;
            currnode = temp; 
        }
        return newhead;

    }
};


