            if(l1 != null) {
                l1 = l1.next;
            }
            if(l2 != null) {
                l2 = l2.next;
            }

            if(sum >= 10) {
                int finalValue = sum % 10;
                carry = sum/10;
                sum = finalValue;
            }

            // Add in sum-list
            sumList = insertNode(sumList, sum);
            if(l1 == null && l2 == null && carry > 0) {
                sumList = insertNode(sumList, carry);
            } 
        }

        return sumList;
