    public ListNode insertNode(ListNode node, int data) {
        ListNode newNode = new ListNode(data);

        if(node == null) {
            node = newNode;
            return node;
        }

        ListNode last = node;
        while(last.next != null) {
            last = last.next;
        }
        last.next = newNode;
        return node;
    }

