            else {

                if(s[i] == ')'){

                    if(!stack.empty() && stack.top() == '('){
                        stack.pop();
                        i++;}

                    else return false;

                }
                if(s[i] == ']'){

                    if(!stack.empty() && stack.top() == '[' ){
                        stack.pop();
                        i++;}

                    else return false;

                }  
                if(s[i] == '}'){

                    if(!stack.empty() && stack.top() == '{'){
                        stack.pop();
                        i++;}

                    else return false;
                }
            }
        }
        if(stack.empty())
            return true;
        else return false;
    }
};
