    func isValid(_ s: String) -> Bool {
        var stack = [Character]()        
        let dict: [Character: Character] = ["(": ")", "{": "}", "[": "]"]

        for char in s {
            switch char {
            case "(", "{", "[": stack.append(char)
            case ")", "}", "]": guard !stack.isEmpty, dict[stack.removeLast()] == char else { return false }
