class Solution {

    /**
     * @param String $s
     * @return Boolean
     */
    function isValid($s) {
        if(strlen($s)%2!=0){
            return false;
        }

        $process_arr = array();
        $s_arr = str_split($s);
        foreach($s_arr as $i){
            switch($i){
                case '(':
                    array_push($process_arr,')');
                    break;
                case '{':
                    array_push($process_arr,'}');
                    break;
                case '[':
                    array_push($process_arr,']');
                    break;
                default:
                    if(empty($process_arr) || $i != $process_arr[count($process_arr)-1]){
                        return false;
                    }else{
                        array_pop($process_arr);
                    }
                    break;

            }
        }

        return count($process_arr)==0;

    }
}

