class Solution {
public:

    // mapping each opening and closing bracket with int opposite in sign for ease check
    unordered_map<char,int> symbols = {{'(',-1},{'{',-2},{'[',-3},{']',3},{'}',2},{')',1}};

    bool isValid(string s) {
        // if string's size is odd
        if(s.size() & 1) return false;

        // stack to store open brackets
        stack<char> st;
        // traversing string
        for (char bracket: s){
            // => we got open bracket
            if (symbols[bracket] < 0){
                st.push(bracket);
            }
            // => we got close bracket
            else
            {
                // CASE 1 : if we've closed bracket but no open bracket in stack
                if (st.empty()) return false;
                // CASE 2 : else than case 1
                // storing top char from stack in a variable named top
                char top = st.top();
                // after retrieving top element from stack, remove it from stack
                st.pop();
                // check if sum equals 0 for valid pair
                if (symbols[top] + symbols[bracket] != 0){
                    return false;
                }
            }
        }
        // after checking for each char of stack if it's not empty => we've extra opening
        //      bracket, so we return false;
        if (!st.empty()) return false;
        return true;
    }
};

