```c++
bool isValid(string s) {
        char last = s[0];
        string current_string = "";
        current_string = current_string + s[0];
        for(int index = 1; index < s.size(); index++)
        {
            if(last == '(' && s[index] == ')')
            {
                current_string.pop_back();
                last = '-';
            }
            else if(last == '[' && s[index] == ']')
            {
                current_string.pop_back();
                last = '-';
            }
            else if(last == '{' && s[index] == '}')
            {
                current_string.pop_back();
                last = '-';
            }
            else
            {
                last = s[index];
                current_string = current_string + s[index];
            }
            if(last == '-' && current_string.size() > 0)
            {
                last = current_string[current_string.size()-1];
            }
        }
        return current_string.size() == 0 ? true : false;
    }
