class Solution {
    public boolean isValid(String str) {
        Stack<Character> s=new Stack<>();
        for(int i=0;i<str.length();i++){
            char cur=s.charAt(i);
            if(isOpening(cur)){
                s.push(cur);
            }else{
                if(s.isEmpty()){
                    retutn false;
                }else-if(!isMatching(s.peek(),cur)){
                    return false;
                }else{
                    s.pop();
                }
            }
        }
        return isEmpty();
    }
    static boolean isOpening(char c){
        return c='(' || '{' || '[';
    }
    static boolean isMatching(char a, char b){
     return  (a='()' && b=')') || (a='{' || b='}') || (a='[' || b=']');
    }
}

