class Solution {
      private static boolean isMatchingPair(char c1, char c2) {
    return (c1 == '(' && c2 == ')') || (c1 == '{' && c2 == '}') || (c1 == '[' && c2 == ']');
  }
    public boolean isValid(String s) {
    Stack<Character> stack = new Stack<Character>();
    int n = s.length() - 1;
    for (int i = 0; i <= n; i++) {
      if (s.charAt(i) == '(' || s.charAt(i) == '{' || s.charAt(i) == '[') {
        stack.push(s.charAt((i)));
      } else if (s.charAt(i) == ')' || s.charAt(i) == '}' || s.charAt(i) == ']') {
        if (stack.isEmpty() || !isMatchingPair(stack.pop(), s.charAt(i))) {
          return false;
        }
      }
    }
    if(!stack.isEmpty()) {
        return false;
    }

    return true;

  }
}

class Solution {
public:
    bool isValid(string s) {
        stack<char> st;
        if(s[0]==']' || s[0]=='}' || s[0]==')'){
            return false;
        }
        for(int i=0;s[i]!='\0';i++){
            if(s[i]=='(' || s[i]=='{' || s[i]=='['){
                      st.push(s[i]);
            }
            else if(st.empty()){
                return false;
            }
            else if(st.top()=='(' && s[i]==')' || st.top()=='{' && s[i]=='}' || st.top()=='[' && s[i]==']'){
                            st.pop();
            }
            else
               return false;


        }

        if(st.empty())
            return true;
        return false;
    }
};

