            TreeNode root2 = queue.poll();
            if(root1==null && root2==null) continue;
            if(root1==null || root2==null || root1.val!=root2.val) return false;
            queue.add(root1.left);
            queue.add(root2.left);
            queue.add(root1.right);
            queue.add(root2.right);
        }
        return true;
    }
}
