class Solution:
    def isSameTree(self, p, q):
        """
        :type p: TreeNode
        :type q: TreeNode
        :rtype: bool
        """
        stack = [(p, q)]

        while stack:
            p, q = stack.pop()

            if not p and not q:
                continue
            if not p or not q or p.val != q.val:
                return False
            stack.append((p.right, q.right))
            stack.append((p.left, q.left))

        return True
