var isSameTree = function(p, q) {
	// A FLAG VARIABLE TO KEEP TRACK OF IF THE TREES ARE SAME OR NOT
    let isSame = true;

	// IF THE ROOT NODES ARE BOTH NULL THEN THEY'RE THE SAME TREE
    if (!p && !q) {
        return true;
    }

	// IF EITHER OF THE ROOT NODES ARE NULL AND THE OTHER ISNT, THEN THEY'RE NOT THE SAME TREE
    if ((p && !q) || (!p && q)) {
        return false;
    }

	// HELPER TRAVERSAL FUNCTION FOR DEPTH FIRST SEARCH
	// TAKES IN THE NODES FROM BOTH TREES AS PARAMETERS
    let traverse = (a, b) => {

		// IF THE TWO NODES VALUES ARE NOT THE SAME, THEN THEY'RE NOT THE SAME TREE
        if (a.val !== b.val) {
            isSame = false;
        }

		// IF BOTH TREES HAVE LEFT NODES, RUN HELPER FUNCTION
        if (a.left && b.left) {
            traverse(a.left, b.left)
        }

		// IF BOTH TREES HAVE RIGHT NODES, RUN HELPER FUNCTION
        if (a.right && b.right) {
            traverse(a.right, b.right)
        }

		// IF ONE TREE HAS A LEFT NODE AND THE OTHER DOESN'T, THEY'RE NOT THE SAME TREE
        if ((a.left && !b.left) || (!a.left && b.left)) {
            isSame = false
        }

		// IF ONE TREE HAS A RIGHT NODE AND THE OTHER DOESN'T, THEY'RE NOT THE SAME TREE
        if ((a.right && !b.right) || (!a.right && b.right)) {
            isSame = false;
        }
    }

	// INVOKE THE HELPER FUNCTION WITH THE ROOT NODES
    traverse(p, q)

    return isSame;
};
```

